function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu");document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger")):(t.classList.add("active"),e.classList.add("active-burger"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"))}))}burgerMenu();const counterTotal=document.getElementById("counter-total");document.addEventListener("click",(function(e){let t=e.target;if(!t.closest(".js-counter-btn"))return;let c=t.closest(".js-counter").querySelector(".js-counter-value");switch(t.getAttribute("data-action")){case"plus":if(Number(c.value)>8)return c.value;c.value=Number(c.value)+1,counterTotal.innerHTML=.01*c.value+" ETH";break;case"minus":if(Number(c.value)<=1)return c.value;c.value=Number(c.value)-1,counterTotal.innerHTML=.01*c.value+" ETH";break;case"max":c.value=9,counterTotal.innerHTML=.01*c.value+" ETH"}}));var swiper=new Swiper(".mySwiper",{breakpoints:{320:{slidesPerView:1,spaceBetween:15,slidesPerGroup:1},480:{slidesPerView:2,spaceBetween:15},768:{slidesPerView:2,spaceBetween:20},992:{slidesPerView:3,spaceBetween:20},1200:{slidesPerView:4,spaceBetween:20},1450:{slidesPerView:4,spaceBetween:30}},loop:!1,loopFillGroupWithBlank:!1,pagination:{el:".swiper-pagination",clickable:!0}});function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const t=e.parentNode;t.classList.contains("accordion__item-active")?t.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),t.classList.add("accordion__item-active"))}))}))}accordion();