function burgerMenu(){const e=document.querySelector(".burger"),i=document.querySelector(".menu");document.querySelector("body");e.addEventListener("click",(()=>{i.classList.contains("active")?(i.classList.remove("active"),e.classList.remove("active-burger")):(i.classList.add("active"),e.classList.add("active-burger"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(i.classList.remove("active"),e.classList.remove("active-burger"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav),document.addEventListener("click",(function(e){let i=e.target;if(!i.closest(".js-counter-btn"))return;let t=i.closest(".js-counter").querySelector(".js-counter-value");switch(i.getAttribute("data-action")){case"plus":if(Number(t.value)>8)return t.value;t.value=Number(t.value)+1;break;case"minus":if(Number(t.value)<1)return t.value;t.value=Number(t.value)-1;break;case"max":t.value=9}}));var swiper=new Swiper(".mySwiper",{breakpoints:{320:{slidesPerView:1,spaceBetween:15,slidesPerGroup:1},480:{slidesPerView:2,spaceBetween:15},768:{slidesPerView:2,spaceBetween:20},992:{slidesPerView:3,spaceBetween:20},1200:{slidesPerView:4,spaceBetween:20},1450:{slidesPerView:4,spaceBetween:30}},loop:!1,loopFillGroupWithBlank:!1,pagination:{el:".swiper-pagination",clickable:!0}});function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const i=e.parentNode;i.classList.contains("accordion__item-active")?i.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),i.classList.add("accordion__item-active"))}))}))}accordion();